mapper.insert:

SELECT NVL(MAX(bno), 0) + 1
FROM tbl_board_01

INSERT INTO tbl_board_01
			(
			 bno
			 , title
			 , writer
			 , contents
			 , image
			)
VALUES (
		#{bno}
		, #{title}
		, #{writer}
		, #{contents}
		, #{image}
		)

mapper.update:
UPDATE tbl_board_01
SET 
	title = #{title},
	contents = #{contents}
	writer = #{writer},
	updatedate = sysdate
	image = #{image}
WHERE bno = #{bno}
